class Solution:
 def minOperations(self, arr: List[int], k: int) -> int:
 ans = max(arr)
 for x in range((ans + 1) // 2):
 cnt = sum(
 abs(a - b) <= k and (a - b > 0 or a == b) 
 for a, b in combinations(arr, 2)
 )
 if cnt == len(arr) * (len(arr) - 1) // 2:
 return ans + x * 2
 return -1
